import React from 'react';

interface AnimatedAirplaneLogoProps {
  size?: 'sm' | 'md' | 'lg';
  className?: string;
}

export default function AnimatedAirplaneLogo({ size = 'md', className = '' }: AnimatedAirplaneLogoProps) {
  const sizeClasses = {
    sm: 'w-10 h-10',
    md: 'w-14 h-14',
    lg: 'w-18 h-18'
  };

  // Base64 encoded animated airplane GIF
  const animatedAirplaneGif = `data:image/gif;base64,`;

  return (
    <div className={`relative ${sizeClasses[size]} ${className} airplane-logo-wrapper`}>
      <div className="airplane-container">
        <img 
          src={animatedAirplaneGif}
          alt="ECL Global Animated Airplane"
          className="w-full h-full object-contain airplane-gif"
        />
      </div>
    </div>
  );
}
